<template>
    <div id="app">
        <v-app id="inspire">
            <div class="text-center">
              <v-icon>mdi-silverware </v-icon>  <h1 class="mb-5">Card치pio</h1>
    
    <v-expansion-panels
        v-model="panel"
        :readonly="readonly"
        multiple
      >


        <v-expansion-panel v-for="product in products" :key="product.id">
          <v-expansion-panel-header>{{ product.title }} <v-spacer></v-spacer> R$ {{ product.price | formatCurrency }}</v-expansion-panel-header>
          <v-expansion-panel-content>
              <div class="d-flex">
                  <div class="mr-5">
                      <v-img contain width="100" :src="product.img"></v-img>
                  </div>
                  <div>
                      Qtd:
                      <input v-model="product.qty" type="number" min="1">
                      <div class="mt-2 d-flex">
                           <v-btn color="success" small @click="addCart(product)" >Adicionar</v-btn>
                            <v-btn class="ml-2" small>Detalhes</v-btn>
                      </div>
                     
                  </div>
              </div>
              
          </v-expansion-panel-content>
        </v-expansion-panel>

      </v-expansion-panels>

            <v-btn
                color="blue"
                dark
                @click="sheet = !sheet"
                block
                fixed
                absolute
                bottom
            >
                <v-icon small class="ml-2">mdi-cart</v-icon>Carrinho ({{totalItems}}) R$ {{Total | formatCurrency}}
            </v-btn>
            <v-bottom-sheet v-model="sheet" persistent>
                <v-sheet
                min-width="375"
                min-height="500"
                class="text-center"
                >
                <v-btn
                    class="mt-6"
                    text
                    @click="sheet = !sheet"
                    icon
                >
                    <v-icon small class="ml-2">mdi-cart</v-icon>
                    Carrinho
                    <v-spacer></v-spacer><v-icon color="red">mdi-close</v-icon>
                </v-btn>
                <div class="py-3">

                    

                    <v-simple-table v-show="cartItems.length > 0" class="table">
                        <template v-slot:default>
                            <tbody>
                            <tr
                                v-for="item in cartItems"
                                :key="item.id"
                            >
                                <td>{{ item.title }}</td>
                                <td>{{ item.qty }} und</td>
                                <td>R$ {{ item.price }}</td>
                                <td @click="removeItem(item)"><v-icon small color="error">mdi-trash-can</v-icon></td>
                            </tr>
                            <tr v-show="cartItems.length > 0">
                                <td></td>
                                <td>Total</td>
                                <td>R$ {{Total | formatCurrency }}</td>
                                <td></td>
                            </tr>
                            </tbody>
                        </template>

                        <v-btn color="primary" class="mt-5">Fazer Pedido</v-btn>

                    </v-simple-table>
                    
                    

                    <div v-show="cartItems.length === 0">
                        <p>Carrinho est치 vazio</p>
                    </div>
                </div>
                </v-sheet>
            </v-bottom-sheet>
            </div>
        </v-app>
    </div>
</template>

<script>
    export default {
        data(){
            return{
                mobileBreakpoint: 600,
                sheet: false,
                cartItems: [],
                products: [
                    {id: 1,title: 'Mocot칩', price: 25.00, qty: 1, delivery: 'Gr치tis', img: 'data:image/jpeg;base64,'},  
                    {id: 2,title: 'Feijoada',price: 25.00, qty: 1, delivery: 'Gr치tis', img: 'data:image/jpeg;base64,'},  
                    {id: 3,title: 'Torta de Lim칚o',price: 12.00, qty: 1, delivery: 'Gr치tis', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT54n1LF2B38HNFy-IBobeaFlzSSFCHtRXfmg&usqp=CAU'},  
                    {id: 4,title: 'Bolo de Pote',price: 5.00, qty: 1, delivery: 'Gr치tis', img: 'data:image/jpeg;base64,'},  
                ]
            }
        },

        computed: {
            totalItems(){
                return this.cartItems.reduce((accumulator , item) => {
                    return accumulator  + item.qty;
                }, 0);
            },
            Total() {
                let total = 0;
                this.cartItems.forEach(item => {
                    total += (item.price * item.qty);
                });
                return total;
            }
        },
        methods:{
            addCart(product){
                let found = false;

                let itemInCart = this.cartItems.filter(item => item.id===product.id);
			    let isItemInCart = itemInCart.length > 0;

                if (isItemInCart === false) {
                    this.cartItems.push(product);
                } else {
                    itemInCart[0].qty ++;
                }
            },

            removeItem(index) {
                this.cartItems.splice(index, 1)
            }
        }
    }
</script>

